import { DataTypes } from 'sequelize';
import sequelize from '../config/dbsql';

const Product = sequelize.define('Product', {
    product_id: {
        type: DataTypes.INTEGER,
        autoIncrement: true,
        primaryKey: true,
    },
    name: {
        type: DataTypes.STRING,
        allowNull: false,
    },
    purchase_price: {
        type: DataTypes.DECIMAL(10, 2),
        allowNull: false,
    },
    sales_price: {
        type: DataTypes.DECIMAL(10, 2),
        allowNull: false,
    },
    profit: {
        type: DataTypes.DECIMAL(10, 2),
        allowNull: false,
        get() {
            const salesPrice = this.getDataValue("sales_price");
            const purchasePrice = this.getDataValue('purchase_price');
            return salesPrice - purchasePrice;
        },
    },
});

export default Product;
